const {Pokemon, PokeItem, PokeMove} = require('../dist/index.js');

describe('fast-poke-fetch usage', () => {
  it('should fetch data for Pikachu', async () => {
    const pokemonData = await Pokemon('pikachu');
    expect(pokemonData).toBeDefined();
  });

  it('should handle unknown PokÃ©mon gracefully', async () => {
    const pokemonData = await Pokemon('peekachoo');
    expect(pokemonData.error).toBe(true);
  expect(pokemonData.message).toBe('An error occurred while fetching data');
  });

  it('should fetch item data for Potion', async () => {
    const itemData = await PokeItem('potion');
    expect(itemData).toBeDefined();
  });

  it('should fetch move data for Tackle', async () => {
    const moveData = await PokeMove('tackle');
    expect(moveData).toBeDefined();
  });

});